    }, {
        tarefa:'javaScript',
        tempo:'01:00:00'
    }, {
        tarefa:'TypeScript',
        tempo:'03:00:00'   
    }]
    return (
        <aside className={style.listaTarefas}>
            <h2 onClick={()=>{

            }}>
                Estudos do dia
            </h2>
            <ul>
                {taferas.map((item, index)=>(
                    <Item
